(function($){Drupal.behaviors.getlocations_leaflet_preview={attach:function(){if(typeof Drupal.settings.getlocations_leaflet==='undefined'){return}$.each(Drupal.settings.getlocations_leaflet,function(key,settings){if(settings.extcontrol=='preview_map'){Drupal.getlocations_leaflet_map[key].on('zoomend',function(){$("#edit-getlocations-leaflet-defaults-zoom").val(Drupal.getlocations_leaflet_map[key].getZoom())});Drupal.getlocations_leaflet_map[key].on('dragend',function(){var ll=Drupal.getlocations_leaflet_map[key].getCenter();$("#edit-getlocations-leaflet-defaults-latlong").val(ll.lat+','+ll.lng)})}})}}}(jQuery));